Ext.define('FP.controller.Start', {
    extend: 'Ext.app.Controller',

    config: {
        routes: {
            'start': 'showStart'
        },

        control: {
            "button#loginButton": {
                tap: 'onLoginButtonTap'
            },
            "button#signUpButton": {
                tap: 'onSignUpButtonTap'
            }
        }
    },

    onLoginButtonTap: function(button, e, eOpts) {
        this.redirectTo('login');
    },

    onSignUpButtonTap: function(button, e, eOpts) {
        this.redirectTo('signup');
    },

    showStart: function() {
        var main = Ext.getCmp('main'),
            start = Ext.create('FP.view.Start');

        Ext.ComponentQuery.query('#toolbar')[0].hide();
        //toolbar.hide();
        main.setActiveItem(start);
    }

});