Ext.define('FP.view.AccountBalance', {
    extend: 'Ext.dataview.List',

    config: {
        itemId: 'accountBalance',
        emptyText: '<img align="middle" src="images/fp.png"  width="75px" /><span class="emptyList">Add transactions.</span>',
        pressedCls: 'fp-item-pressed',
        store: 'accountBalance',
        grouped: true,
        itemTpl: Ext.create('Ext.XTemplate', 
            '<tpl if="amount &lt; 0">',
            '	<div><span style="float: right;">({[this.displayBalance(values.amount)]})</span>{description}<br/></div>',
            '<tpl else>',
            '	<div><span style="float: right;">{[this.displayBalance(values.amount)]}</span>{description}<br/></div>',
            '</tpl>',
            {
                displayBalance: function(balance) {
                    if(balance < 0) {
                        return '$'+Math.abs(balance).toFixed(2);
                    } else {
                        return '$'+balance.toFixed(2);
                    }
                }
            }
        )
    }

});